/*!
 * Toggle Password Visibility
 */
function initializeHideShowPassword(){$(".form-group").find(".form-control").each(function(n,t){var i=$(t);i.parent().find(".glyphicon").click(function(){i.parent().find(":password").focus();$(t).prop("type")==="password"?($(t).prop("type","text"),$(this).text("Hide")):($(t).prop("type","password"),$(this).text("Show"))})})}$(document).ready(initializeHideShowPassword);